import React, { useState } from 'react';
import { Container, Card, CardContent, makeStyles, Grid } from '@material-ui/core';
import QrReader from 'react-qr-reader';
import Button from '@mui/material/Button';
import Beep from '../src/Assets/censor-beep-01.mp3'
import useSound from 'use-sound';


function App() {
  const [scanResult, setScanResult] = useState('');
  const classes = useStyles();
  const [play] = useSound(Beep);

  const handleErrorWebCam = (error) => {
    console.log(error);
  }
  const handleScanWebCam = (result) => {
    if (result) {
      setScanResult(result);
      play()
    }
  }



  return (
    <Container className={classes.conatiner}>
      <Card>
        <h2 className={classes.title}>Qr Scan test</h2>
        <CardContent>

          <Grid>
            <h3>Qr Code Scan</h3>
            <QrReader
              delay={300}
              style={{ width: '100%', align: 'center' }}
              onError={handleErrorWebCam}
              onScan={handleScanWebCam}
            />
            <h3>ผลลัพท์: {scanResult}</h3>
            <Button variant="contained" onClick={() => setScanResult("")}>Clear</Button>
          </Grid>
        </CardContent>
      </Card>
    </Container>
  );
}

const useStyles = makeStyles((theme) => ({
  conatiner: {
    marginTop: 10
  },
  title: {
    display: 'flex',
    justifyContent: 'center',
    alignItems: 'center',
    background: '#E2E3FF',
    color: '#00000',
    padding: 20
  }

}));
export default App;
