import React, { useState } from 'react';
import { Container, Card, CardContent, makeStyles, Grid } from '@material-ui/core';
import QrReader from 'react-qr-reader';
import Button from '@mui/material/Button';
import Beep from '../src/Assets/censor-beep-01.mp3'
import useSound from 'use-sound';
import TextField from '@mui/material/TextField';
import Box from '@mui/material/Box';
import Typography from '@mui/material/Typography';
import Modal from '@mui/material/Modal';
import BarcodeScannerComponent from "react-qr-barcode-scanner";

function App() {
  const [scanResult, setScanResult] = useState('');
  const classes = useStyles();
  const [play] = useSound(Beep);

  const handleErrorWebCam = (error) => {
    console.log(error);
  }
  const handleScanWebCam = (result) => {
    if (result) {
      play()
      setScanResult(result);
      setOpen(false)
    }
  }

  const [open, setOpen] = React.useState(false);
  const handleOpen = () => setOpen(true);
  const handleClose = () => setOpen(false);

  const [stopStream, setStopStream] = useState(false)

  const barcodeScannerComponentHandleUpdate = (result) => {
    if (result) {
      play()
      setScanResult(result);
      setOpen(false)
      setStopStream(true)
    }
  };

  const style = {
    position: 'absolute',
    top: '50%',
    left: '50%',
    transform: 'translate(-50%, -50%)',
    width: 400,
    bgcolor: 'background.paper',
    border: '2px solid #000',
    boxShadow: 24,
    p: 4,
  };
  return (
    <Container className={classes.conatiner}>
      <Card>
        <h2 className={classes.title}>Qr Scan test</h2>
        <CardContent>
          <Box
            component="form"
            sx={{
              '& > :not(style)': { m: 1, width: '25ch' },
            }}
            noValidate
            autoComplete="off"
            align="center"
          >
            <TextField id="standard-basic" variant="standard" value={scanResult} onChange={(e) => setScanResult(e.target.value)} />
            <Button variant="contained" onClick={handleOpen}>Scan Qr Code</Button>
          </Box>

          <Modal
            open={open}
            onClose={handleClose}
            aria-labelledby="modal-modal-title"
            aria-describedby="modal-modal-description"
          >
            <Box sx={style}>
              <Grid>
                <h3>Qr Code Scan</h3>
                {/* <BarcodeScannerComponent
                  delay={300}
                  style={{ width: '%', align: 'center' }}
                  onError={handleErrorWebCam}
                  onScan={handleScanWebCam}
                /> */}

                <BarcodeScannerComponent
                  onUpdate={barcodeScannerComponentHandleUpdate}
                  stopStream={stopStream}
                />
                <h3>ผลลัพท์: {scanResult}</h3>
                <Button variant="contained" onClick={() => setScanResult("")}>Clear</Button>
              </Grid>
            </Box>
          </Modal>



        </CardContent>
      </Card>
    </Container>
  );
}

const useStyles = makeStyles((theme) => ({
  conatiner: {
    marginTop: 10
  },
  title: {
    display: 'flex',
    justifyContent: 'center',
    alignItems: 'center',
    background: '#E2E3FF',
    color: '#00000',
    padding: 20
  }

}));
export default App;
